var CryptoJS = (function() {
    var crypto = {};

    // Simple SHA256 implementation for browser
    crypto.SHA256 = function(message) {
        // Convert string to bytes if needed
        if (typeof message === 'string') {
            message = str2bytes(message);
        }

        // Use Web Crypto API if available, otherwise use fallback
        if (typeof window !== 'undefined' && window.crypto && window.crypto.subtle) {
            return sha256WebCrypto(message);
        } else {
            return sha256Fallback(message);
        }
    };

    // Helper function to convert string to byte array
    function str2bytes(str) {
        var bytes = [];
        for (var i = 0; i < str.length; i++) {
            bytes.push(str.charCodeAt(i));
        }
        return new Uint8Array(bytes);
    }

    // Web Crypto API implementation
    function sha256WebCrypto(buffer) {
        if (typeof buffer === 'string') {
            buffer = str2bytes(buffer);
        }
        
        return crypto.subtle.digest('SHA-256', buffer).then(function(hash) {
            return new Sha256Hash(Array.from(new Uint8Array(hash)));
        });
    }

    // Fallback pure JavaScript implementation of SHA-256
    function sha256Fallback(message) {
        var K = [
            0x428a2f98, 0x71374491, 0xb5c0fbcf, 0xe9b5dba5, 0x3956c25b, 0x59f111f1, 0x923f82a4, 0xab1c5ed5,
            0xd807aa98, 0x12835b01, 0x243185be, 0x550c7dc3, 0x72be5d74, 0x80deb1fe, 0x9bdc06a7, 0xc19bf174,
            0xe49b69c1, 0xefbe4786, 0x0fc19dc6, 0x240ca1cc, 0x2de92c6f, 0x4a7484aa, 0x5cb0a9dc, 0x76f988da,
            0x983e5152, 0xa831c66d, 0xb00327c8, 0xbf597fc7, 0xc6e00bf3, 0xd5a79147, 0x06ca6351, 0x14292967,
            0x27b70a85, 0x2e1b2138, 0x4d2c6dfc, 0x53380d13, 0x650a7354, 0x766a0abb, 0x81c2c92e, 0x92722c85,
            0xa2bfe8a1, 0xa81a664b, 0xc24b8b70, 0xc76c51a3, 0xd192e819, 0xd6990624, 0xf40e3585, 0x106aa070,
            0x19a4c116, 0x1e376c08, 0x2748774c, 0x34b0bcb5, 0x391c0cb3, 0x4ed8aa4a, 0x5b9cca4f, 0x682e6ff3,
            0x748f82ee, 0x78a5636f, 0x84c87814, 0x8cc70208, 0x90befffa, 0xa4506ceb, 0xbef9a3f7, 0xc67178f2
        ];

        function rotateRight(word, bits) {
            return (word >>> bits) | (word << (32 - bits));
        }

        function ch(x, y, z) {
            return (x & y) ^ (~x & z);
        }

        function maj(x, y, z) {
            return (x & y) ^ (x & z) ^ (y & z);
        }

        function sigma0(x) {
            return rotateRight(x, 2) ^ rotateRight(x, 13) ^ rotateRight(x, 22);
        }

        function sigma1(x) {
            return rotateRight(x, 6) ^ rotateRight(x, 11) ^ rotateRight(x, 25);
        }

        function gamma0(x) {
            return rotateRight(x, 7) ^ rotateRight(x, 18) ^ (x >>> 3);
        }

        function gamma1(x) {
            return rotateRight(x, 17) ^ rotateRight(x, 19) ^ (x >>> 10);
        }

        // Pre-processing
        var padding = [0x80];
        while (padding.length % 64 !== 56) {
            padding.push(0x00);
        }

        var ml = message.length * 8;
        for (var i = 0; i < 4; i++) {
            padding.push((ml >>> (24 - i * 8)) & 0xff);
        }

        // Add padding to message
        var m = new Uint8Array(message.length + padding.length);
        m.set(message);
        m.set(padding, message.length);

        // Process the message in successive 512-bit chunks
        var h0 = 0x6a09e667, h1 = 0xbb67ae85, h2 = 0x3c6ef372, h3 = 0xa54ff53a;
        var h4 = 0x510e527f, h5 = 0x9b05688c, h6 = 0x1f83d9ab, h7 = 0x5be0cd19;

        for (var i = 0; i < m.length; i += 64) {
            var w = new Array(64);

            // Break chunk into sixteen 32-bit words
            for (var j = 0; j < 16; j++) {
                w[j] = (m[i + j * 4] << 24) | (m[i + j * 4 + 1] << 16) |
                       (m[i + j * 4 + 2] << 8) | (m[i + j * 4 + 3]);
            }

            // Extend the sixteen 32-bit words into sixty-four 32-bit words
            for (var j = 16; j < 64; j++) {
                w[j] = (gamma1(w[j - 2]) + w[j - 7] + gamma0(w[j - 15]) + w[j - 16]) | 0;
            }

            // Initialize hash value for this chunk
            var a = h0, b = h1, c = h2, d = h3, e = h4, f = h5, g = h6, h = h7;

            // Main loop
            for (var j = 0; j < 64; j++) {
                var t1 = (h + sigma1(e) + ch(e, f, g) + K[j] + w[j]) | 0;
                var t2 = (sigma0(a) + maj(a, b, c)) | 0;

                h = g;
                g = f;
                f = e;
                e = (d + t1) | 0;
                d = c;
                c = b;
                b = a;
                a = (t1 + t2) | 0;
            }

            // Add this chunk's hash to result so far
            h0 = (h0 + a) | 0;
            h1 = (h1 + b) | 0;
            h2 = (h2 + c) | 0;
            h3 = (h3 + d) | 0;
            h4 = (h4 + e) | 0;
            h5 = (h5 + f) | 0;
            h6 = (h6 + g) | 0;
            h7 = (h7 + h) | 0;
        }

        // Produce the final hash value (big-endian)
        var digest = new Uint8Array(32);
        var hashes = [h0, h1, h2, h3, h4, h5, h6, h7];
        for (var i = 0; i < 8; i++) {
            digest[i * 4] = (hashes[i] >>> 24) & 0xff;
            digest[i * 4 + 1] = (hashes[i] >>> 16) & 0xff;
            digest[i * 4 + 2] = (hashes[i] >>> 8) & 0xff;
            digest[i * 4 + 3] = hashes[i] & 0xff;
        }

        return new Sha256Hash(Array.from(digest));
    }

    // Wrapper for hash result to mimic CryptoJS behavior
    function Sha256Hash(words) {
        this.words = words;
    }

    Sha256Hash.prototype.toString = function() {
        // Convert byte array to hex string
        var hex = '';
        for (var i = 0; i < this.words.length; i++) {
            var byte = this.words[i];
            hex += (byte >>> 4).toString(16) + (byte & 0xf).toString(16);
        }
        return hex;
    };

    return crypto;
})();